ai_interactions.student_message:
body {
	background: black;
}
* {
	border: 1px dashed red;
	min-height: 10px;
}
.postcard {
	background: white;
	width: 400px;
	height: 300px;
	margin: auto;
}
.postcard-header {
	display: flex;
}
.postcard-header img {
	width: 50px;
	height: 50px;
}
.postcard-header h1 {
	width: 500px;
	margin: none;
}
.postcard-body {
	display: flex;
}
.body-left p:first-child {
	margin-top: none;
}
.body-left,
.body-right {
	padding: 10px;
}
.input {
	border: none;
	border-bottom: grey;
	text-decoration: overline gray;
}
.p {
	font-size: medium;
}
.postcard-footer {
	text-align: center;
}
.button {
	background-color: darkgray;
	padding: 500px;
	font-family: Arial;
}
